INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2013-12-10 19:51:20', '2013-12-10 19:51:20', 'Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2013-12-10 19:51:20', '2013-12-10 19:51:20', '', 0, 'http://127.0.0.1/exol/?p=1', 0, 'post', '', 1),
(2, 1, '2013-12-10 19:51:20', '2013-12-10 19:51:20', 'This is an example page. It''s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I''m a bike messenger by day, aspiring actor by night, and this is my blog. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin'' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href="http://127.0.0.1/exol/wp-admin/">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'open', 'open', '', 'sample-page', '', '', '2013-12-10 19:51:20', '2013-12-10 19:51:20', '', 0, 'http://127.0.0.1/exol/?page_id=2', 0, 'page', '', 0),
(3, 1, '2013-12-10 19:51:32', '0000-00-00 00:00:00', '', 'Auto Draft', '', 'auto-draft', 'open', 'open', '', '', '', '', '2013-12-10 19:51:32', '0000-00-00 00:00:00', '', 0, 'http://127.0.0.1/exol/?p=3', 0, 'post', '', 0),
(4, 1, '2013-12-10 19:55:02', '2013-12-10 19:55:02', '[subsection color="dark" full_width="0" full_height="0" background="http://us-themes.com/wp/CorsaXML/wp-content/uploads/parallax.png" parallax="1"]\r\n\r\n[home_heading]\r\n[heading_line type="white" new_line="0"]Wonderful Digital Things[/heading_line]\r\n[heading_line type="white" new_line="1"]and a pinch of creativity[/heading_line]\r\n[/home_heading]\r\n\r\n[button url="#about" text="Learn More" size="big" type="primary" icon="" external="0"]\r\n\r\n[/subsection]', 'Corsa Home', '', 'publish', 'open', 'open', '', 'corsa-home', '', '', '2013-12-10 20:35:28', '2013-12-10 20:35:28', '', 0, 'http://127.0.0.1/exol/?page_id=4', 0, 'page', '', 0),
(5, 1, '2013-12-10 19:55:02', '2013-12-10 19:55:02', '', 'Corsa Home', '', 'inherit', 'open', 'open', '', '4-revision-v1', '', '', '2013-12-10 19:55:02', '2013-12-10 19:55:02', '', 4, 'http://127.0.0.1/exol/?p=5', 0, 'revision', '', 0),
(6, 1, '2013-10-14 13:20:32', '2013-10-14 13:20:32', '<h2><strong>About Corsa</strong></h2>\n[separator type="short" size="" icon="heart"]\n\n[subtitle]CORSA is an [highlight]<strong>award winning</strong>[/highlight] interactive agency\nworking for [highlight]<strong>leading brands</strong>[/highlight] worldwide[/subtitle]\n\n[separator type="invisible" size="" icon="star"]\n\n[row]\n[one_half]\n<p style="text-align: left;">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec suscipit nulla mi, ut pellentesque felis fermentum et. Quisque nec est euismod, egestas lorem nec, volutpat nunc. Phasellus neque lectus, convallis non consectetur non, molestie ac enim. Suspendisse et tincidunt urna.</p>\n[/one_half]\n[one_half]\n<p style="text-align: left;">Donec ante justo, scelerisque a iaculis vitae, accumsan nec risus. Integer quis lacinia ligula. Sed id nunc interdum, lobortis arcu sit amet, interdum libero. Integer adipiscing, metus vel molestie tempus, magna tortor adipiscing lorem, accumsan accumsan arcu arcu nec metus.</p>\n[/one_half]\n[/row]\n\n[subsection color="alternate" full_width="0" full_height="0" background="" parallax="0" parallax_speed="0.2"]\n\n[subtitle]What we do?[/subtitle]\n<h3>[highlight]<strong>Powerful websites that look beautiful</strong>[/highlight]</h3>\n[separator type="invisible" size="" icon="star"]\n\n[row]\n[one_third][iconbox iconpos="top" icon="rocket" with_circle="1" title="From Zero to One" link="" img=""]We are moving from zero to one, not from one to hundred. That means we are focused on creating new solutions, not copying the existing ones.[/iconbox][/one_third]\n[one_third][iconbox iconpos="top" icon="thumbs-up" with_circle="1" title="Our Level Up" link="" img=""]Working with us you will work with professional certified designers and engineers having the vast good experience.[/iconbox][/one_third]\n[one_third][iconbox iconpos="top" icon="heart" with_circle="1" title="Your satisfaction" link="" img=""]Creating a design it is not an end in itself, but a means. We understand the rules of the games. It’s about to make you happy.[/iconbox][/one_third]\n[/row]\n\n[separator type="invisible" size="" icon="star"]\n\n[button url="#portfolio" text="View works" size="" type="default" icon="arrow-down" external="0"]\n\n[/subsection]\n<h4>How about Some Fun Facts about our agency?</h4>\n[separator type="invisible" size="small" icon="star"]\n\n[row]\n[one_quarter] [counter count="345" title="Pizzas ordered"] [/one_quarter]\n[one_quarter] [counter count="87" title="Clients we''ve worked with"] [/one_quarter]\n[one_quarter] [counter count="126" title="Projects completed"] [/one_quarter]\n[one_quarter] [counter count="56" title="Magazines published"] [/one_quarter]\n[/row]\n\n[subsection color="dark" full_width="0" full_height="0" background="http://127.0.0.1/exol/wp-content/uploads/2013/10/parallax.png" parallax="1"]\n<h2>CORSA is a <strong>creative studio</strong> dedicated\nto crafting <strong>meaninful image</strong></h2>\n[/subsection]', 'About', '', 'publish', 'closed', 'closed', '', 'about', '', '', '2013-10-14 13:20:32', '2013-10-14 13:20:32', '', 0, 'http://us-themes.com/wp/Corsa/?post_type=us_main_page_section&amp;p=6', 0, 'us_main_page_section', '', 0),
